<template>
  <v-card flat>
    <v-card-title primary-title>
      <div class="headline">
        注册
      </div>
    </v-card-title>
    <v-container>
      <v-layout>
        <v-flex>
          <v-form v-model="valid" lazy-validation>
            <v-text-field
              prepend-icon="person"
              label="用户名"
              v-model="username"
              required
              :rules="[rules.required]"
            />
            <v-text-field
              prepend-icon="lock"
              label="密码"
              type="password"
              v-model="password"
              required
              :rules="[rules.required]"
            />
            <v-text-field
              prepend-icon="lock"
              label="再次输入密码"
              type="password"
              v-model="pwdRepeat"
              required
              :rules="[rules.required, () => password === pwdRepeat || '两次密码必须相同']"
            />
            <v-row>
              <v-col>
                <span v-html="captchaSvg" />
              </v-col>
              <v-col>
                <v-text-field
                  prepend-icon="verified_user"
                  label="验证码"
                  v-model="captchaText"
                  required
                  :rules="[rules.required]"
                >
                  <template v-slot:append>
                    <v-btn icon>
                      <v-icon>refresh</v-icon>
                    </v-btn>
                  </template>
                </v-text-field>
              </v-col>
            </v-row>
            <v-row>
              <v-col>
                <v-btn color="info" block :disabled="!valid">
                  注册
                </v-btn>
              </v-col>
              <v-col>
                <v-btn block to="/login">
                  去登录
                </v-btn>
              </v-col>
            </v-row>
          </v-form>
        </v-flex>
      </v-layout>
    </v-container>
  </v-card>
</template>

